import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { 
      temp: undefined,
    };
  }

  callAPI() {
    fetch("http://localhost:9000/getWeather?lat=52.356496&lon=4.952130")
      .then(res => res.json)
      .then(res => this.setState({ temp: res.main.temp }));
  }

  componentWillMount() {
    this.callAPI();
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <p className="App-intro">
            Temp: {this.state.temp}
          </p>
        </header>
      </div>
    );
  }
}

export default App;
